// Include helper functions

function extractHealthMetrics(text) {
  const health = {
    sleep_hours: null,
    sleep_quality: null,
    stress_level: null,
    caffeine_intake: null,
    water_liters: null,
    meals_count: null,
    energy_crashes: null,
    mental_clarity: null
  };

  // Sleep extraction
  const sleepPatterns = [
    /(\d+(?:\.\d+)?)\s*(?:hours?|hrs?)\s*(?:of\s*)?sleep/i,
    /slept\s*(?:for\s*)?(\d+(?:\.\d+)?)\s*(?:hours?|hrs?)/i,
    /got\s*(?:only\s*)?(\d+(?:\.\d+)?)\s*(?:hours?|hrs?)/i,
    /(\d+(?:\.\d+)?)\s*(?:hours?|hrs?)\s*last night/i
  ];

  for (const pattern of sleepPatterns) {
    const match = text.match(pattern);
    if (match) {
      health.sleep_hours = parseFloat(match[1]);
      break;
    }
  }

  // Sleep quality from context
  if (text.match(/\b(terrible|awful|bad|poor)\s*(sleep|night)/i)) health.sleep_quality = 2;
  else if (text.match(/\b(great|excellent|good|well)\s*(sleep|night)/i)) health.sleep_quality = 8;
  else if (text.match(/\b(okay|decent|fine)\s*(sleep|night)/i)) health.sleep_quality = 5;
  else if (health.sleep_hours && health.sleep_hours < 5) health.sleep_quality = 3;
  else if (health.sleep_hours && health.sleep_hours > 8) health.sleep_quality = 7;

  // Stress level extraction - MUST CATCH "stress level is at 9"
  const stressPatterns = [
    /stress\s*(?:level\s*)?(?:is\s*)?(?:at\s*)?(\d+)/i,
    /(\d+)(?:\/10)?\s*stress/i,
    /stress.*?(\d+)/i
  ];

  for (const pattern of stressPatterns) {
    const match = text.match(pattern);
    if (match) {
      health.stress_level = parseInt(match[1]);
      break;
    }
  }

  // Contextual stress detection
  if (!health.stress_level) {
    if (text.match(/\b(overwhelmed|breaking point|can't handle)\b/i)) health.stress_level = 9;
    else if (text.match(/\b(very stressed|extremely stressed|so stressed)\b/i)) health.stress_level = 8;
    else if (text.match(/\b(stressed|anxious|pressure)\b/i)) health.stress_level = 7;
    else if (text.match(/\b(calm|relaxed|peaceful)\b/i)) health.stress_level = 3;
  }

  // Caffeine extraction - MUST CATCH "5 coffees"
  const caffeinePatterns = [
    /(\d+)\s*(?:cups?\s*of\s*)?(?:coffee|espresso|latte|cappuccino|caffeine)/i,
    /had\s*(\d+)\s*coffee/i,
    /coffee.*?(\d+)/i
  ];

  for (const pattern of caffeinePatterns) {
    const match = text.match(pattern);
    if (match) {
      health.caffeine_intake = parseInt(match[1]);
      break;
    }
  }

  // Water intake
  const waterPatterns = [
    /(\d+(?:\.\d+)?)\s*(?:liters?|litres?|l)\s*(?:of\s*)?water/i,
    /drank\s*(\d+(?:\.\d+)?)\s*(?:liters?|litres?)/i,
    /(\d+)\s*(?:glasses?|cups?)\s*(?:of\s*)?water/i
  ];

  for (const pattern of waterPatterns) {
    const match = text.match(pattern);
    if (match) {
      if (match[0].includes('glass') || match[0].includes('cup')) {
        health.water_liters = parseFloat(match[1]) * 0.25; // assume 250ml per glass
      } else {
        health.water_liters = parseFloat(match[1]);
      }
      break;
    }
  }

  // Meal counting
  const meals = [];
  if (text.match(/\bbreakfast\b/i)) meals.push('breakfast');
  if (text.match(/\blunch\b/i)) meals.push('lunch');
  if (text.match(/\bdinner\b/i)) meals.push('dinner');
  if (text.match(/\bsnack/i)) meals.push('snack');

  // Check for skipped meals
  const skippedPattern = /(?:skipped|missed|no)\s*(breakfast|lunch|dinner)/gi;
  const skippedMatches = [...text.matchAll(skippedPattern)];
  for (const match of skippedMatches) {
    const index = meals.indexOf(match[1].toLowerCase());
    if (index > -1) meals.splice(index, 1);
  }

  health.meals_count = meals.length || null;

  // Energy crashes
  if (text.match(/\b(crash|crashed|energy dip|afternoon slump|3\s*pm\s*slump)\b/i)) {
    if (text.match(/\bafternoon|3\s*pm|after lunch\b/i)) health.energy_crashes = 'afternoon';
    else if (text.match(/\bmorning\b/i)) health.energy_crashes = 'morning';
    else health.energy_crashes = 'yes';
  }

  // Mental clarity
  if (text.match(/\b(focused|sharp|clear|productive)\b/i)) health.mental_clarity = 8;
  else if (text.match(/\b(foggy|fuzzy|confused|scattered)\b/i)) health.mental_clarity = 3;
  else if (text.match(/\b(okay|decent|manageable)\b/i)) health.mental_clarity = 5;

  return health;
}

const userInput = data.user_input || '';
const healthData = extractHealthMetrics(userInput);
const emotions = extractDeepEmotionalSignals(userInput);

// Merge extracted data with router data
const sleepHours = data.sleep_hours || healthData.sleep_hours;
const stressLevel = data.stress_level || healthData.stress_level;
const caffeineIntake = data.caffeine_intake || healthData.caffeine_intake;

// Calculate confidence
let confidence = data.confidence_level || 0.5;
if (sleepHours && stressLevel && caffeineIntake) confidence = 0.9;
else if ((sleepHours && stressLevel) || (sleepHours && caffeineIntake)) confidence = 0.8;
else if (sleepHours || stressLevel || caffeineIntake) confidence = 0.7;

return {
  user_input: data.user_input || null,
  SleepHours: sleepHours,
  'Calorie intake': parseFloat(data.calorie_intake || data.calories) || null,
  'Vitamin intake': data.vitamin_intake || data.vitamins || data.supplements || null,
  sleep_hours: sleepHours,
  sleep_quality: healthData.sleep_quality || parseFloat(data.sleep_quality) || null,
  meals_count: healthData.meals_count || parseFloat(data.meals_count) || null,
  water_liters: healthData.water_liters || parseFloat(data.water_liters) || null,
  caffeine_intake: caffeineIntake,
  stress_level: stressLevel,
  screen_time_hours: parseFloat(data.screen_time_hours || data.screen_time) || null,
  energy_crashes: healthData.energy_crashes || data.energy_crashes || null,
  took_breaks: data.took_breaks !== undefined ? data.took_breaks : null,
  mental_clarity: healthData.mental_clarity || parseFloat(data.mental_clarity) || null,
  confidence_level: confidence,
  emotional_signals: JSON.stringify(emotions)
};

