// Include helper functions

function extractGoalMetrics(text) {
  const goal = {
    target_value: null,
    current_value: null,
    deadline: null,
    unit_of_measurement: null,
    goal_type: null
  };

  // Money patterns - MUST CATCH "$30k" and "currently at $18k"
  const moneyPatterns = [
    /(?:goal|target|aiming for|need|want)\s*(?:is\s*)?(?:to\s*)?(?:hit|reach|make|earn)?\s*\$?(\d+(?:,\d{3})*(?:\.\d+)?k?m?)/i,
    /\$(\d+(?:,\d{3})*(?:\.\d+)?k?m?)\s*(?:revenue|sales|income|profit)/i,
    /(\d+(?:,\d{3})*(?:\.\d+)?k?m?)\s*(?:dollars|usd|\$)/i
  ];

  for (const pattern of moneyPatterns) {
    const match = text.match(pattern);
    if (match) {
      let value = match[1].replace(/,/g, '');
      if (value.includes('k')) value = parseFloat(value.replace('k', '')) * 1000;
      else if (value.includes('m')) value = parseFloat(value.replace('m', '')) * 1000000;
      else value = parseFloat(value);
      goal.target_value = value;
      goal.unit_of_measurement = 'dollars';
      goal.goal_type = 'financial';
      break;
    }
  }

  // Current value extraction
  const currentPatterns = [
    /currently\s*(?:at|have|earned|made)?\s*\$?(\d+(?:,\d{3})*(?:\.\d+)?k?m?)/i,
    /(?:so far|already|now)\s*(?:at|have)?\s*\$?(\d+(?:,\d{3})*(?:\.\d+)?k?m?)/i,
    /(?:at|have)\s*\$?(\d+(?:,\d{3})*(?:\.\d+)?k?m?)\s*(?:now|already|so far)/i
  ];

  for (const pattern of currentPatterns) {
    const match = text.match(pattern);
    if (match) {
      let value = match[1].replace(/,/g, '');
      if (value.includes('k')) value = parseFloat(value.replace('k', '')) * 1000;
      else if (value.includes('m')) value = parseFloat(value.replace('m', '')) * 1000000;
      else value = parseFloat(value);
      goal.current_value = value.toString();
      break;
    }
  }

  // Deadline extraction - MUST CATCH "end of month"
  const dateTime = extractDateTime(text);
  if (dateTime.date) {
    goal.deadline = dateTime.date;
  }

  // Additional deadline patterns
  if (!goal.deadline) {
    const deadlinePatterns = {
      'by end of month': () => {
        const d = new Date();
        d.setMonth(d.getMonth() + 1);
        d.setDate(0);
        return d.toISOString().split('T')[0];
      },
      'this month': () => {
        const d = new Date();
        d.setMonth(d.getMonth() + 1);
        d.setDate(0);
        return d.toISOString().split('T')[0];
      },
      'this week': () => {
        const d = new Date();
        const day = d.getDay();
        const diff = 7 - day;
        d.setDate(d.getDate() + diff);
        return d.toISOString().split('T')[0];
      },
      'this year': () => {
        const d = new Date();
        d.setMonth(11);
        d.setDate(31);
        return d.toISOString().split('T')[0];
      },
      'by next month': () => {
        const d = new Date();
        d.setMonth(d.getMonth() + 2);
        d.setDate(0);
        return d.toISOString().split('T')[0];
      }
    };

    for (const [phrase, dateFunc] of Object.entries(deadlinePatterns)) {
      if (text.toLowerCase().includes(phrase)) {
        goal.deadline = dateFunc();
        break;
      }
    }
  }

  // Goal type detection
  if (!goal.goal_type) {
    if (text.match(/\b(revenue|sales|income|profit|money|financial|earn)\b/i)) {
      goal.goal_type = 'financial';
    } else if (text.match(/\b(weight|health|fitness|workout|exercise)\b/i)) {
      goal.goal_type = 'health';
    } else if (text.match(/\b(customer|client|user|subscriber)\b/i)) {
      goal.goal_type = 'business';
    } else if (text.match(/\b(product|launch|ship|release|build)\b/i)) {
      goal.goal_type = 'product';
    } else if (text.match(/\b(habit|routine|daily|weekly)\b/i)) {
      goal.goal_type = 'habits';
    }
  }

  return goal;
}

