function extractInsecurities(text) {
  const insecurities = [];

  const insecurityPatterns = {
    imposter_syndrome: /\b(imposter|fraud|fake|not qualified|don't belong|pretending)\b/i,
    self_doubt: /\b(not good enough|never enough|can't do|incapable|incompetent)\b/i,
    comparison: /\b(everyone else|others are better|behind|falling behind|not as good)\b/i,
    perfectionism: /\b(not perfect|mistakes|flawed|failure|mess up)\b/i,
    abandonment: /\b(leave me|abandon|alone|nobody cares|forgotten)\b/i,
    rejection: /\b(reject|unwanted|don't like me|hate me|push away)\b/i,
    inadequacy: /\b(inadequate|insufficient|lacking|missing|not enough)\b/i,
    shame: /\b(ashamed|embarrassed|humiliated|disgrace|shameful)\b/i
  };

  for (const [insecurity, pattern] of Object.entries(insecurityPatterns)) {
    if (pattern.test(text)) {
      insecurities.push(insecurity.replace(/_/g, ' '));
    }
  }

  // Deep linguistic analysis for implicit insecurities
  if (text.match(/\b(I'm|i am)\s+(always|never|just|only)\b/i)) {
    insecurities.push('fixed mindset');
  }

  if (text.match(/\b(should|supposed to|meant to|have to)\b/gi)?.length > 2) {
    insecurities.push('external validation seeking');
  }

  if (text.match(/\bbut\s*(I'm|i am|I|i)\s*(not|can't|don't)\b/i)) {
    insecurities.push('self-limiting beliefs');
  }

  return insecurities;
}

function extractFears(text) {
  const fears = [];

  const fearPatterns = {
    failure: /\b(fail|failure|failing|unsuccessful|not succeed)\b/i,
    success: /\b(success|succeed|achieving|what if I make it)\b/i,
    judgment: /\b(judge|judged|criticism|criticize|what.*think)\b/i,
    rejection: /\b(reject|rejection|turn down|say no|declined)\b/i,
    abandonment: /\b(leave|abandon|alone|lost|lose them)\b/i,
    inadequacy: /\b(not enough|inadequate|insufficient|lacking)\b/i,
    unknown: /\b(unknown|uncertain|don't know what|unpredictable)\b/i,
    loss: /\b(lose|loss|losing|lost|gone)\b/i,
    change: /\b(change|different|new|unfamiliar)\b/i,
    commitment: /\b(commit|commitment|trapped|stuck with)\b/i,
    exposure: /\b(exposed|found out|discover|reveal)\b/i,
    poverty: /\b(broke|poor|no money|bankrupt|financial ruin)\b/i
  };

  for (const [fear, pattern] of Object.entries(fearPatterns)) {
    if (pattern.test(text)) {
      fears.push(fear);
    }
  }

  // Context-specific fear detection
  if (text.match(/\bafraid\s*(of|that|to)\s*(\w+)/i)) {
    const match = text.match(/\bafraid\s*(of|that|to)\s*(\w+)/i);
    if (match && !fears.includes(match[2])) {
      fears.push(match[2]);
    }
  }

  return fears;
}

