// Include helper functions

function parseBoolean(val) {
  if (val === true || val === 'true' || val === 1) return true;
  if (val === false || val === 'false' || val === 0) return false;
  return null;
}

function calculateHappinessImpact(emotions, text) {
  let impact = 5; // baseline

  // Positive modifiers
  if (emotions.includes('happy') || emotions.includes('proud')) impact += 3;
  if (text.match(/\b(great|wonderful|amazing|quality time)\b/i)) impact += 2;
  if (text.match(/\b(connected|bonding|together)\b/i)) impact += 1;

  // Negative modifiers
  if (emotions.includes('guilty')) impact -= 3;
  if (emotions.includes('sad') || emotions.includes('frustrated')) impact -= 2;
  if (text.match(/\b(missed|skipped|absent|couldn't)\b/i)) impact -= 2;
  if (text.match(/\b(argument|fight|conflict|tension)\b/i)) impact -= 3;
  if (text.match(/\b(again|always|keeps happening)\b/i)) impact -= 1;

  return Math.max(1, Math.min(10, impact));
}

