// Include helper functions

function parseMoneyValue(val) {
  if (!val) return null;
  if (typeof val === 'number') return val;
  const cleaned = val.toString().replace(/[$,]/g, '');
  if (cleaned.toLowerCase().includes('k')) {
    return parseFloat(cleaned.replace(/k/i, '')) * 1000;
  }
  if (cleaned.toLowerCase().includes('m')) {
    return parseFloat(cleaned.replace(/m/i, '')) * 1000000;
  }
  return parseFloat(cleaned) || null;
}

function parseBoolean(val) {
  if (val === true || val === 'true' || val === 1) return true;
  if (val === false || val === 'false' || val === 0) return false;
  return null;
}

function extractTimeSaved(text) {
  const patterns = [
    /saving\s*(?:me\s*)?(\d+)\s*(hours?|hrs?)\s*(?:per|a|each)?\s*(week|day|month)/i,
    /save\s*(\d+)\s*(hours?|hrs?)\s*(?:per|a|each)?\s*(week|day|month)/i,
    /(\d+)\s*(hours?|hrs?)\s*(?:per|a|each)?\s*(week|day|month)\s*saved/i,
    /freed?\s*up\s*(\d+)\s*(hours?|hrs?)/i
  ];

  for (const pattern of patterns) {
    const match = text.match(pattern);
    if (match) {
      let hours = parseInt(match[1]);
      const period = match[3]?.toLowerCase();

      // Normalize to hours per week
      if (period === 'day') hours *= 7;
      else if (period === 'month') hours /= 4;

      return hours;
    }
  }
  return null;
}

