      performance.whatBlocked.push(sentence.trim());
    }

    // Tomorrow mentions
    if (s.match(/\b(tomorrow|next|upcoming|scheduled)\b/)) {
      performance.tomorrowHurdle.push(sentence.trim());
    }
  }

  // Extract specific achievements
  if (text.match(/\b(closed|sold)\s*(\d+)?\s*(deals?|clients?|sales?)/i)) {
    const match = text.match(/\b(closed|sold)\s*(\d+)?\s*(deals?|clients?|sales?)/i);
