function extractDeepEmotionalSignals(text) {
  const emotions = [];
  const emotionalIntensity = {};

  // Linguistic markers
  const emotionPatterns = {
    // Anxiety spectrum
    anxious: {
      keywords: ['anxious', 'worried', 'nervous', 'uneasy', 'tense', 'on edge', 'restless'],
      phrases: ['can\'t stop thinking', 'what if', 'keeps me up', 'racing thoughts'],
      punctuation: ['...', '??', '!!!'],
      intensifiers: ['really', 'so', 'very', 'extremely', 'totally']
    },
    stressed: {
      keywords: ['stressed', 'overwhelmed', 'pressure', 'burnt out', 'exhausted', 'drained'],
      phrases: ['too much', 'can\'t handle', 'breaking point', 'falling apart'],
      linguistic: ['shouty caps', 'fragmented sentences', 'repetition']
    },
    frustrated: {
      keywords: ['frustrated', 'annoyed', 'irritated', 'pissed', 'fed up', 'sick of'],
      phrases: ['why does this', 'always happens', 'never works', 'tired of'],
      punctuation: ['!', '...', 'CAPS']
    },
    sad: {
      keywords: ['sad', 'depressed', 'down', 'low', 'blue', 'miserable', 'hopeless'],
      phrases: ['no point', 'given up', 'can\'t anymore', 'empty'],
      linguistic: ['past tense dominance', 'negative absolutes']
    },
