function extractFitnessData(text) {
  const fitness = {
    activity: null,
    distance: null,
    duration: null,
    intensity: null,
    time_of_day: null,
    energy_before: null,
    energy_after: null,
    workout_time: null
  };

  // Activity detection with variations
  const activityPatterns = {
    running: /\b(ran|run|running|jog|jogging|sprint|sprinted)\b/i,
    walking: /\b(walk|walked|walking|stroll|hiking|hiked)\b/i,
    cycling: /\b(bike|biked|cycling|cycle|rode|riding)\b/i,
    swimming: /\b(swim|swam|swimming|laps)\b/i,
    gym: /\b(gym|workout|lifted|lifting|weights|strength)\b/i,
    yoga: /\b(yoga|stretch|stretched|flexibility)\b/i,
    cardio: /\b(cardio|hiit|circuit|crossfit)\b/i
  };

  for (const [activity, pattern] of Object.entries(activityPatterns)) {
    if (pattern.test(text)) {
      fitness.activity = activity;
      break;
    }
  }

  // Distance extraction with units
  const distancePatterns = [
    { regex: /(\d+(?:\.\d+)?)\s*(miles?|mi\.?)/i, unit: 'miles' },
    { regex: /(\d+(?:\.\d+)?)\s*(kilometers?|km)/i, unit: 'km' },
    { regex: /(\d+(?:\.\d+)?)\s*(meters?|m)\b/i, unit: 'meters' },
    { regex: /(\d+(?:\.\d+)?)\s*k\b/i, unit: 'km' }
  ];

  for (const pattern of distancePatterns) {
    const match = text.match(pattern.regex);
    if (match) {
      fitness.distance = parseFloat(match[1]);
      fitness.distance_unit = pattern.unit;
      break;
    }
  }

  // Duration extraction
  const durationMatch = text.match(/(\d+)\s*(hours?|hrs?|minutes?|mins?)/i);
  if (durationMatch) {
    const value = parseInt(durationMatch[1]);
    const unit = durationMatch[2].toLowerCase();
    fitness.duration = unit.includes('hour') ? value * 60 : value;
  }

  // Time of day extraction
  const timeOfDayPatterns = {
    morning: /\b(morning|dawn|sunrise|am|5\s*am|6\s*am|7\s*am|8\s*am)\b/i,
    afternoon: /\b(afternoon|lunch|noon|midday|pm)\b/i,
    evening: /\b(evening|dusk|sunset|night|6\s*pm|7\s*pm|8\s*pm)\b/i
  };

  for (const [timeOfDay, pattern] of Object.entries(timeOfDayPatterns)) {
    if (pattern.test(text)) {
      fitness.time_of_day = timeOfDay;
      break;
    }
  }

  // Energy level detection
  const energyPatterns = {
    high: /\b(energized|pumped|great|amazing|fantastic|ready)\b/i,
    medium: /\b(okay|fine|decent|alright)\b/i,
    low: /\b(tired|exhausted|drained|sluggish|slow)\b/i
  };

  for (const [level, pattern] of Object.entries(energyPatterns)) {
    if (pattern.test(text)) {
      fitness.energy_after = level === 'high' ? 8 : level === 'medium' ? 5 : 3;
      break;
    }
  }

  // Intensity detection
  if (text.match(/\b(intense|hard|tough|brutal|killer)\b/i)) fitness.intensity = 8;
  else if (text.match(/\b(moderate|decent|good)\b/i)) fitness.intensity = 5;
  else if (text.match(/\b(easy|light|gentle|slow)\b/i)) fitness.intensity = 3;

  // Extract actual workout time
  const dateTime = extractDateTime(text);
  if (dateTime.time) fitness.workout_time = dateTime.time;

  return fitness;
}

