    negation: /\b(not|never|no|can't|won't|don't|didn't)\b/gi,
    absolutism: /\b(always|never|everyone|nobody|everything|nothing)\b/gi,
    self_criticism: /\b(I'm|i am)\s+(stupid|dumb|worthless|failure|loser)/gi
  };

  for (const [marker, pattern] of Object.entries(linguisticMarkers)) {
    const matches = text.match(pattern);
    if (matches && matches.length > 2) {
      if (marker === 'desperation') emotions.push('desperate');
      if (marker === 'uncertainty') emotions.push('uncertain');
      if (marker === 'self_criticism') emotions.push('self-critical');
    }
  }

  // Analyze sentence structure
  const sentences = text.split(/[.!?]+/);
  const avgSentenceLength = sentences.reduce((sum, s) => sum + s.split(' ').length, 0) / sentences.length;

  if (avgSentenceLength < 5 && sentences.length > 2) {
    emotions.push('fragmented-thinking');
  }

  if (text.match(/[A-Z]{3,}/g)) {
    emotions.push('shouting');
    if (!emotions.includes('frustrated')) emotions.push('frustrated');
  }

  // Time-based emotional markers
  if (textLower.match(/\b(again|always|keeps happening|every time)\b/)) {
    emotions.push('pattern-frustrated');
  }

  if (textLower.match(/\b(finally|at last|about time)\b/)) {
    emotions.push('relief');
  }

  return [...new Set(emotions)];
}

// ADVANCED DATE/TIME EXTRACTION
function extractDateTime(text) {
  const now = new Date();
  const results = {
    date: null,
    time: null,
    timestamp: null
  };

  // Time patterns
  const timePatterns = [
    { regex: /(\d{1,2}):(\d{2})\s*(am|pm|AM|PM)/i, handler: (m) => {
      let hour = parseInt(m[1]);
      const min = parseInt(m[2]);
      const meridiem = m[3].toLowerCase();
      if (meridiem === 'pm' && hour < 12) hour += 12;
      if (meridiem === 'am' && hour === 12) hour = 0;
      return `${hour.toString().padStart(2, '0')}:${min.toString().padStart(2, '0')}:00`;
    }},
    { regex: /(\d{1,2})\s*(am|pm|AM|PM)/i, handler: (m) => {
      let hour = parseInt(m[1]);
      const meridiem = m[2].toLowerCase();
      if (meridiem === 'pm' && hour < 12) hour += 12;
      if (meridiem === 'am' && hour === 12) hour = 0;
      return `${hour.toString().padStart(2, '0')}:00:00`;
    }},
    { regex: /(\d{1,2}):(\d{2})/i, handler: (m) => {
      return `${m[1].padStart(2, '0')}:${m[2]}:00`;
    }}
  ];

  for (const pattern of timePatterns) {
    const match = text.match(pattern.regex);
    if (match) {
      results.time = pattern.handler(match);
      break;
    }
  }

  // Date extraction with context
  const dateContext = {
    'today': () => now,
    'tomorrow': () => new Date(now.getTime() + 24*60*60*1000),
    'yesterday': () => new Date(now.getTime() - 24*60*60*1000),
    'next week': () => new Date(now.getTime() + 7*24*60*60*1000),
    'last week': () => new Date(now.getTime() - 7*24*60*60*1000),
    'next month': () => {
      const d = new Date(now);
      d.setMonth(d.getMonth() + 1);
      return d;
    },
    'last month': () => {
      const d = new Date(now);
      d.setMonth(d.getMonth() - 1);
      return d;
    },
    'end of month': () => {
      const d = new Date(now);
      d.setMonth(d.getMonth() + 1);
      d.setDate(0);
      return d;
    },
    'end of week': () => {
      const d = new Date(now);
      const day = d.getDay();
      const diff = 7 - day;
      d.setDate(d.getDate() + diff);
      return d;
    }
  };

  for (const [phrase, dateFunc] of Object.entries(dateContext)) {
    if (text.toLowerCase().includes(phrase)) {
      const date = dateFunc();
      results.date = date.toISOString().split('T')[0];
      if (results.time) {
        results.timestamp = `${results.date}T${results.time}`;
      }
      break;
    }
  }

  // "in X days/weeks/months" pattern
  const relativePattern = /in\s+(\d+)\s+(days?|weeks?|months?|hours?)/i;
  const relMatch = text.match(relativePattern);
  if (relMatch) {
    const num = parseInt(relMatch[1]);
    const unit = relMatch[2].toLowerCase();
    const futureDate = new Date(now);

    if (unit.startsWith('hour')) {
      futureDate.setHours(futureDate.getHours() + num);
    } else if (unit.startsWith('day')) {
      futureDate.setDate(futureDate.getDate() + num);
    } else if (unit.startsWith('week')) {
      futureDate.setDate(futureDate.getDate() + (num * 7));
    } else if (unit.startsWith('month')) {
      futureDate.setMonth(futureDate.getMonth() + num);
    }

    results.date = futureDate.toISOString().split('T')[0];
    results.timestamp = futureDate.toISOString();
  }

  // Day of week detection
  const daysOfWeek = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'];
  const dayMatch = text.toLowerCase().match(new RegExp(`(next|this|last)\\s+(${daysOfWeek.join('|')})`, 'i'));
  if (dayMatch) {
    const modifier = dayMatch[1];
    const targetDay = daysOfWeek.indexOf(dayMatch[2]);
    const currentDay = now.getDay();
    let daysToAdd = targetDay - currentDay;

    if (modifier === 'next') {
      if (daysToAdd <= 0) daysToAdd += 7;
    } else if (modifier === 'last') {
      if (daysToAdd >= 0) daysToAdd -= 7;
    }

    const targetDate = new Date(now);
    targetDate.setDate(targetDate.getDate() + daysToAdd);
    results.date = targetDate.toISOString().split('T')[0];
  }

  return results;
}

// MONEY VALUE PARSER
function parseMoneyValue(value) {
  if (!value) return null;

  // Remove common currency symbols and formatting
  let cleanValue = value.toString().replace(/[$,Â£â‚¬Â¥]/g, '');

  // Handle 'k' notation (thousands)
  if (cleanValue.toLowerCase().includes('k')) {
    cleanValue = cleanValue.toLowerCase().replace('k', '');
    return parseFloat(cleanValue) * 1000;
  }

  // Handle 'm' notation (millions)
  if (cleanValue.toLowerCase().includes('m')) {
    cleanValue = cleanValue.toLowerCase().replace('m', '');
    return parseFloat(cleanValue) * 1000000;
  }

  return parseFloat(cleanValue) || null;
}

