function extractBusinessModelMetrics(text) {
  const metrics = {};

  // Revenue model patterns
  const revenuePatterns = [
    /(?:revenue|making|earning).*?\$?([0-9,]+(?:\.[0-9]+)?k?)/i,
    /\$?([0-9,]+(?:\.[0-9]+)?k?).*?(?:revenue|income|profit)/i,
    /(?:monthly|per month).*?\$?([0-9,]+(?:\.[0-9]+)?k?)/i
  ];
  for (const pattern of revenuePatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.monthly_revenue = parseMoneyValue(match[1]);
      break;
    }
  }

  // Customer count - "500 customers", "250 users", "1k clients"
  const customerPatterns = [
    /([0-9,]+k?)\s*(?:customers?|users?|clients?|buyers?)/i,
    /(?:have|got|serving).*?([0-9,]+k?).*?(?:customers?|users?|clients?)/i,
    /([0-9,]+k?).*?(?:active|paying).*?(?:customers?|users?)/i
  ];
  for (const pattern of customerPatterns) {
    const match = text.match(pattern);
    if (match) {
      let count = match[1].replace(/,/g, '');
      if (count.includes('k')) {
        count = parseFloat(count.replace('k', '')) * 1000;
      } else {
        count = parseInt(count);
      }
      metrics.customer_count = count;
      break;
    }
  }

  // Unit economics - CLTV, CAC
  const cltvPatterns = [
    /(?:cltv|lifetime value|ltv).*?\$?([0-9,]+(?:\.[0-9]+)?k?)/i,
    /\$?([0-9,]+(?:\.[0-9]+)?k?).*?(?:cltv|lifetime|ltv)/i
  ];
  for (const pattern of cltvPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.customer_lifetime_value = parseMoneyValue(match[1]);
      break;
    }
  }

  const cacPatterns = [
    /(?:cac|acquisition cost|customer cost).*?\$?([0-9,]+(?:\.[0-9]+)?k?)/i,
    /\$?([0-9,]+(?:\.[0-9]+)?k?).*?(?:cac|acquisition|to acquire)/i
  ];
  for (const pattern of cacPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.customer_acquisition_cost = parseMoneyValue(match[1]);
      break;
    }
  }

  // Profit margin
  const marginPatterns = [
    /([0-9.]+)%?\s*(?:margin|profit)/i,
    /(?:margin|profit).*?([0-9.]+)%/i,
    /([0-9.]+)\s*percent.*?(?:margin|profit)/i
  ];
  for (const pattern of marginPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.profit_margin_percentage = parseFloat(match[1]);
      break;
    }
  }

  // Growth rate
  const growthPatterns = [
    /([0-9.]+)%?\s*(?:growth|growing|increase)/i,
    /(?:growth|growing).*?([0-9.]+)%/i,
    /(?:up|increased).*?([0-9.]+)%/i,
    /([0-9.]+)%?\s*(?:mom|yoy|monthly|yearly)/i
  ];
  for (const pattern of growthPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.growth_rate_percentage = parseFloat(match[1]);
      break;
    }
  }

  return metrics;
}

