function extractEcommerceMetrics(text) {
  const metrics = {};

  // AOV patterns - "$45 aov", "average order 67", "aov is $32"
  const aovPatterns = [
    /(?:aov|average order|order value).*?\$?([\d,]+(?:\.\d+)?)/i,
    /\$?([\d,]+(?:\.\d+)?).*?(?:aov|average order)/i,
    /(?:orders averaging|avg order).*?\$?([\d,]+(?:\.\d+)?)/i
  ];
  for (const pattern of aovPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.average_order_value = parseMoneyValue(match[1]);
      break;
    }
  }

  // Conversion rate - "3.2%", "3.2 percent", "converting at 3%"
  const convPatterns = [
    /([\d.]+)%?\s*(?:conversion|converting|convert)/i,
    /(?:conversion|converting).*?([\d.]+)%/i,
    /([\d.]+)\s*percent.*?conver/i
  ];
  for (const pattern of convPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.conversion_rate = parseFloat(match[1]);
      break;
    }
  }

  // Cart abandonment - "70% abandon", "cart abandonment 65%"
  const cartPatterns = [
    /([\d.]+)%?\s*(?:abandon|cart abandon|left cart)/i,
    /(?:abandon|cart abandon).*?([\d.]+)%/i,
    /(?:leaving|abandoning).*?([\d.]+)%/i
  ];
  for (const pattern of cartPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.cart_abandonment_rate = parseFloat(match[1]);
      break;
    }
  }

  // Inventory count - "500 products", "250 items in stock"
  const invPatterns = [
    /(\d+)\s*(?:products|items|sku|skus|listings)/i,
    /(?:inventory|stock).*?(\d+)/i,
    /(\d+).*?(?:in stock|available)/i
  ];
  for (const pattern of invPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.inventory_count = parseInt(match[1]);
      break;
    }
  }

  // Return rate
  const returnPatterns = [
    /([\d.]+)%?\s*(?:return|returns|refund)/i,
    /(?:return|refund).*?([\d.]+)%/i
  ];
  for (const pattern of returnPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.return_rate = parseFloat(match[1]);
      break;
