function extractSaasMetrics(text) {
  const metrics = {};

  // MRR patterns - "mrr is $5k", "$3200 mrr", "monthly recurring 2500"
  const mrrPatterns = [
    /(?:mrr|monthly recurring).*?\$?([\d,]+(?:\.\d+)?k?)/i,
    /\$?([\d,]+(?:\.\d+)?k?).*?(?:mrr|monthly recurring)/i,
    /(?:recurring revenue).*?\$?([\d,]+(?:\.\d+)?k?)/i
  ];
  for (const pattern of mrrPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.monthly_recurring_revenue = parseMoneyValue(match[1]);
      break;
    }
  }

  // ARR patterns
  const arrPatterns = [
    /(?:arr|annual recurring).*?\$?([\d,]+(?:\.\d+)?k?)/i,
    /\$?([\d,]+(?:\.\d+)?k?).*?(?:arr|annual recurring)/i
  ];
  for (const pattern of arrPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.annual_recurring_revenue = parseMoneyValue(match[1]);
      break;
    }
  }

  // Churn rate - "5% churn", "churn is 3.2%", "losing 4% monthly"
  const churnPatterns = [
    /([\d.]+)%?\s*(?:churn|cancel|churning|leaving)/i,
    /(?:churn|cancel|turnover).*?([\d.]+)%/i,
    /(?:losing|lost).*?([\d.]+)%.*?(?:monthly|users|customers)/i
  ];
  for (const pattern of churnPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.churn_rate = parseFloat(match[1]);
      break;
    }
  }

  // Active subscribers - "500 subscribers", "250 paying users"
  const subPatterns = [
    /(\d+)\s*(?:subscribers|users|customers|paying|active)/i,
    /(?:have|got).*?(\d+).*?(?:subscribers|users|customers)/i
  ];
  for (const pattern of subPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.active_subscribers = parseInt(match[1]);
      break;
    }
  }

  // New signups - "50 new signups", "25 registrations this week"
  const signupPatterns = [
    /(\d+)\s*(?:new|fresh)?\s*(?:signups|registrations|users|sign.?ups)/i,
    /(?:signed up|registered).*?(\d+)/i,
    /(\d+).*?(?:joined|new users)/i
  ];
  for (const pattern of signupPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.new_signups = parseInt(match[1]);
      break;
    }
  }

  // Trial conversion - "15% trial conversion", "convert 12% of trials"
  const trialPatterns = [
    /([\d.]+)%?\s*(?:trial.*convert|convert.*trial)/i,
    /(?:trial|free).*?convert.*?([\d.]+)%/i,
    /([\d.]+)%.*?(?:trials|free users).*?(?:convert|upgrade)/i
  ];
  for (const pattern of trialPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.trial_to_paid_conversion = parseFloat(match[1]);
      break;
    }
  }

  // User activation rate
  const activationPatterns = [
    /([\d.]+)%?\s*(?:activation|activated|complete onboarding)/i,
    /(?:onboarding|activation).*?([\d.]+)%/i
  ];
  for (const pattern of activationPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.user_activation_rate = parseFloat(match[1]);
      break;
    }
  }

  return metrics;
}

