function extractContentMetrics(text) {
  const metrics = {};

  // Engagement rate - "3.5% engagement", "engaging at 2.8%"
  const engPatterns = [
    /([\d.]+)%?\s*(?:engagement|engage|engaging)/i,
    /(?:engagement|engage).*?([\d.]+)%/i,
    /([\d.]+)\s*percent.*?engag/i
  ];
  for (const pattern of engPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.engagement_rate = parseFloat(match[1]);
      break;
    }
  }

  // Follower count - "10k followers", "2500 subs", "5,000 subscribers"
  const followerPatterns = [
    /([\d,]+k?)\s*(?:followers|subs|subscribers|following)/i,
    /(?:have|got).*?([\d,]+k?).*?(?:followers|subs|subscribers)/i,
    /([\d,]+k?)\s*(?:people|audience)/i
  ];
  for (const pattern of followerPatterns) {
    const match = text.match(pattern);
    if (match) {
      let count = match[1].replace(/,/g, '');
      if (count.includes('k')) {
        count = parseFloat(count.replace('k', '')) * 1000;
      } else {
        count = parseInt(count);
      }
      metrics.follower_count = count;
      break;
    }
  }

  // Content pieces - "posted 15 videos", "created 8 articles"
  const contentPatterns = [
    /(?:posted|created|made|published).*?(\d+).*?(?:posts|videos|articles|pieces|content)/i,
    /(\d+)\s*(?:posts|videos|articles|pieces|reels|stories)/i,
    /(\d+).*?(?:pieces of content|content pieces)/i
  ];
  for (const pattern of contentPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.content_pieces_created = parseInt(match[1]);
      break;
    }
  }

  // Reach/Impressions - "reached 50k", "100k impressions"
  const reachPatterns = [
    /(?:reached|reach).*?([\d,]+k?)/i,
    /([\d,]+k?)\s*(?:impressions|views|reach)/i,
    /(?:impressions|views).*?([\d,]+k?)/i
  ];
  for (const pattern of reachPatterns) {
    const match = text.match(pattern);
    if (match) {
      let count = match[1].replace(/,/g, '');
      if (count.includes('k')) {
        count = parseFloat(count.replace('k', '')) * 1000;
      } else {
        count = parseInt(count);
      }
      metrics.reach_impressions = count;
      break;
    }
  }

  // Click-through rate
  const ctrPatterns = [
    /([\d.]+)%?\s*(?:ctr|click.*through|click.*rate)/i,
    /(?:ctr|click.*through).*?([\d.]+)%/i
  ];
  for (const pattern of ctrPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.click_through_rate = parseFloat(match[1]);
      break;
    }
  }

  return metrics;
}

