function extractAgencyMetrics(text) {
  const metrics = {};

  // Client count - "have 8 clients", "working with 12 clients"
  const clientPatterns = [
    /(?:have|got|working with|managing).*?(\d+).*?clients?/i,
    /(\d+)\s*(?:active\s*)?clients?/i,
    /(\d+)\s*(?:customers|accounts)/i
  ];
  for (const pattern of clientPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.client_count = parseInt(match[1]);
      break;
    }
  }

  // Active projects
  const projectPatterns = [
    /(\d+)\s*(?:active\s*)?projects?/i,
    /(?:working on|managing).*?(\d+).*?projects?/i,
    /(\d+)\s*(?:ongoing|current).*?projects?/i
  ];
  for (const pattern of projectPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.active_projects = parseInt(match[1]);
      break;
    }
  }

  // Average project value
  const valuePatterns = [
    /(?:projects average|avg project|typical project).*?\$?([\d,]+(?:\.\d+)?k?)/i,
    /\$?([\d,]+(?:\.\d+)?k?).*?(?:per project|each project|project value)/i,
    /(?:charging|billing).*?\$?([\d,]+(?:\.\d+)?k?)/i
  ];
  for (const pattern of valuePatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.average_project_value = parseMoneyValue(match[1]);
      break;
    }
