  // Client retention rate
  const retentionPatterns = [
    /([\d.]+)%?\s*(?:retention|stay|retain)/i,
    /(?:retain|keeping).*?([\d.]+)%.*?clients/i,
    /([\d.]+)%.*?clients.*?(?:stay|remain)/i
