const data = $json.data;
const userInput = data.user_input || '';
const emotions = extractDeepEmotionalSignals(userInput);

function extractOperationsMetrics(text) {
  const metrics = {};

  // Error rate
  const errorPatterns = [
    /([\d.]+)%?\s*(?:error|mistakes|wrong|failed)/i,
    /(?:error|mistake).*?([\d.]+)%/i,
    /([\d.]+)\s*(?:out of|in)\s*(\d+).*?(?:error|wrong)/i
  ];
  for (const pattern of errorPatterns) {
    const match = text.match(pattern);
    if (match) {
      if (match[3]) {
        // "3 out of 100" format
        metrics.error_rate = (parseFloat(match[1]) / parseFloat(match[2])) * 100;
      } else {
        metrics.error_rate = parseFloat(match[1]);
      }
      break;
    }
  }

  // Response time
  const responsePatterns = [
    /([\d.]+)\s*(?:hours?|hrs?|minutes?|mins?)\s*(?:response|reply|respond)/i,
    /(?:response|reply).*?([\d.]+)\s*(?:hours?|hrs?|minutes?|mins?)/i,
    /(?:within|under|less than)\s*([\d.]+)\s*(?:hours?|hrs?)/i
  ];
  for (const pattern of responsePatterns) {
    const match = text.match(pattern);
    if (match) {
      let time = parseFloat(match[1]);
      if (text.includes('minute') || text.includes('min')) {
        time = time / 60; // Convert to hours
      }
      metrics.customer_service_response_time_hours = time;
      break;
    }
  }

  // System integrations
  const integrationPatterns = [
    /(\d+)\s*(?:integrations|tools|systems|connected|apps)/i,
    /(?:using|have|got).*?(\d+).*?(?:tools|systems|integrations)/i,
    /(?:connected|integrated).*?(\d+)/i
  ];
  for (const pattern of integrationPatterns) {
    const match = text.match(pattern);
    if (match) {
      metrics.system_integrations = parseInt(match[1]);
      break;
    }
  }

  return metrics;
}

// Automation level detection (1-10 scale)
let automationLevel = 5; // default medium
if (userInput.match(/\b(fully automated|completely automated|100% automated)\b/i)) {
  automationLevel = 9;
} else if (userInput.match(/\b(mostly automated|highly automated|90%|80%)\b/i)) {
  automationLevel = 7;
} else if (userInput.match(/\b(some automation|partially automated|50%)\b/i)) {
  automationLevel = 5;
} else if (userInput.match(/\b(little automation|barely automated|20%|30%)\b/i)) {
  automationLevel = 3;
} else if (userInput.match(/\b(manual|no automation|all manual|0%)\b/i)) {
  automationLevel = 1;
}

// Process documentation completeness (1-10 scale)
let processDoc = 5; // default medium
if (userInput.match(/\b(well documented|fully documented|everything documented|complete docs)\b/i)) {
  processDoc = 9;
} else if (userInput.match(/\b(mostly documented|good documentation)\b/i)) {
  processDoc = 7;
} else if (userInput.match(/\b(some documentation|partially documented)\b/i)) {
  processDoc = 5;
} else if (userInput.match(/\b(little documentation|poor docs)\b/i)) {
  processDoc = 3;
} else if (userInput.match(/\b(no documentation|not documented|all in my head|zero docs)\b/i)) {
  processDoc = 1;
}

// Outsourced functions detection
const outsourced = [];
if (userInput.match(/\b(?:outsourced|hired|delegated).*?accounting\b/i) || userInput.match(/\baccountant\b/i)) {
  outsourced.push('accounting');
}
if (userInput.match(/\b(?:outsourced|hired|delegated).*?(?:support|customer service)\b/i) || userInput.match(/\bva.*support\b/i)) {
  outsourced.push('customer_service');
}
if (userInput.match(/\b(?:outsourced|hired|delegated).*?fulfillment\b/i) || userInput.match(/\b3pl\b/i)) {
  outsourced.push('fulfillment');
}
if (userInput.match(/\b(?:outsourced|hired|delegated).*?marketing\b/i) || userInput.match(/\bmarketing agency\b/i)) {
  outsourced.push('marketing');
}

// Backup systems
let backupSystems = null;
if (userInput.match(/\b(backup|backups|disaster recovery|redundancy)\b/i)) {
  if (userInput.match(/\b(no backup|no backups|not backed up)\b/i)) {
    backupSystems = false;
  } else {
    backupSystems = true;
  }
}

// Time spent on admin
let adminTime = null;
const adminMatch = userInput.match(/(\d+)\s*(?:hours?|hrs?)\s*(?:admin|administrative|paperwork)/i);
if (adminMatch) adminTime = parseFloat(adminMatch[1]);

const metrics = extractOperationsMetrics(userInput);

let confidence = data.confidence_level || 0.5;
const metricCount = Object.keys(metrics).length;
if (automationLevel !== 5 && processDoc !== 5 && metricCount >= 1) confidence = 0.9;
else if ((automationLevel !== 5 || processDoc !== 5) || metricCount >= 1) confidence = 0.8;
else if (outsourced.length > 0) confidence = 0.7;

return {
  user_input: userInput,
  automation_level: automationLevel,
  system_integrations: metrics.system_integrations || null,
  process_documentation_completeness: processDoc,
  error_rate: metrics.error_rate || null,
  customer_service_response_time_hours: metrics.customer_service_response_time_hours || null,
  backup_systems_in_place: backupSystems,
  outsourced_functions: outsourced.length > 0 ? outsourced : null,
  time_spent_on_admin_hours: adminTime,
  confidence_level: confidence,
  emotional_signals: JSON.stringify(emotions)
};

