// ===== CONFIDENCE SCORING 2.0 - LINGUISTIC APPROACH =====
const DIRECTNESS_SCORES = {
  EXPLICIT: 1.0,      // "I sold 5 deals"
  REPORTED: 0.85,     // "My team sold 5 deals"  
  IMPLIED: 0.7,       // "Finally hit my numbers"
