  CONTRADICTORY: 0.1  // "Failed but feeling great!"
};

const MODALITY_MODIFIERS = {
  certain: { 'definitely': 1.0, 'absolutely': 1.0, 'certainly': 0.95, 'obviously': 0.95 },
  probable: { 'probably': 0.7, 'likely': 0.7, 'should': 0.6, 'usually': 0.65 },
  possible: { 'might': 0.5, 'maybe': 0.5, 'could': 0.4, 'perhaps': 0.4, 'possibly': 0.45 },
  doubtful: { 'doubt': 0.3, 'unlikely': 0.3, 'barely': 0.2, 'hardly': 0.25 }
};

const TEMPORAL_CONFIDENCE = {
  'finally': 0.9,    // Strong completion signal
  'again': 0.8,      // Pattern confirmation
  'still': 0.7,      // Ongoing state
  'always': 0.9,     // Strong pattern
  'never': 0.9,      // Strong negation
  'sometimes': 0.5,  // Inconsistent pattern
  'lately': 0.6,     // Recent but not permanent
  'used to': 0.4     // Past state, may have changed
};

function hasConflictingEmotions(emotions) {
  const positive = ['happy', 'excited', 'proud', 'motivated'];
  const negative = ['sad', 'frustrated', 'anxious', 'guilty'];

  const hasPositive = emotions.some(e => positive.includes(e));
  const hasNegative = emotions.some(e => negative.includes(e));

  return hasPositive && hasNegative;
}

function calculateFieldConfidence(text, field, value, extractionType) {
  let confidence = 0.5; // Start neutral

  // 1. DIRECTNESS CHECK
  if (field === 'deal_value' && text.match(/\$\d+/)) {
    confidence = DIRECTNESS_SCORES.EXPLICIT;
  } else if (field === 'sleep_hours' && text.match(/\d+\s*hours?\s*(?:of\s*)?sleep/)) {
    confidence = DIRECTNESS_SCORES.EXPLICIT;
  } else if (field === 'deal_stage' && text.includes('closed')) {
    confidence = DIRECTNESS_SCORES.REPORTED;
  } else if (extractionType === 'emotion_inference') {
    confidence = DIRECTNESS_SCORES.IMPLIED;
  } else if (extractionType === 'pattern_match') {
    confidence = DIRECTNESS_SCORES.INFERRED;
  }

  // 2. MODALITY MODIFIERS
  for (const [certainty, words] of Object.entries(MODALITY_MODIFIERS)) {
    for (const [word, score] of Object.entries(words)) {
      if (text.includes(word)) {
        confidence *= score;
        break; // Only apply one modifier
      }
    }
  }

  // 3. TEMPORAL MODIFIERS
  for (const [word, modifier] of Object.entries(TEMPORAL_CONFIDENCE)) {
    if (text.includes(word)) {
      confidence *= modifier;
      break;
    }
  }

  // 4. NEGATION PENALTY
  const negationBeforeValue = new RegExp(`(not|never|didn't|won't|can't)\\s+\\w*\\s*${value}`, 'i');
  if (negationBeforeValue.test(text)) {
    confidence *= 0.7;
  }

  // 5. QUESTION MARKS = UNCERTAINTY
  if (text.includes('?')) {
    confidence *= 0.8;
  }

  // 6. MULTIPLE NUMBERS = AMBIGUITY
  const numbers = text.match(/\d+/g);
  if (numbers && numbers.length > 2) {
    confidence *= 0.85;
  }

  return Math.max(0.1, Math.min(confidence, 1.0));
}

function calculateRippleConfidence(primaryConfidence, rippleDistance) {
  const RIPPLE_DECAY = {
    1: 0.9,  // Direct cause-effect
    2: 0.7,  // One step removed
    3: 0.5,  // Two steps removed
    4: 0.3   // Distant connection
  };

  return primaryConfidence * (RIPPLE_DECAY[rippleDistance] || 0.2);
}

// ===== HELPER FUNCTIONS =====
function cleanTypos(text) {
  let cleaned = text.toLowerCase();
  for (const [typo, correct] of Object.entries(TYPO_MAP)) {
    const regex = new RegExp(`\\b${typo}\\b`, 'gi');
    cleaned = cleaned.replace(regex, correct);
  }
  return cleaned;
}

function parseMoneyValue(text) {
  for (const pattern of MONEY_PATTERNS) {
    const matches = [...text.matchAll(pattern)];
    if (matches.length > 0) {
      const match = matches[0];
      let value = parseFloat(match[1].replace(/,/g, ''));

      if (match[0].toLowerCase().includes('k') || match[0].toLowerCase().includes('thousand')) {
        value *= 1000;
      } else if (match[0].toLowerCase().includes('m') || match[0].toLowerCase().includes('million')) {
        value *= 1000000;
      } else if (match[0].toLowerCase().includes('grand')) {
        value *= 1000;
      }

      return value;
    }
  }
  return null;
}

function parseTime(text) {
  const match = text.match(/(\d{1,2}):?(\d{2})?\s*(am|pm)/i);
  if (!match) return null;

  let hour = parseInt(match[1]);
  const minute = match[2] ? parseInt(match[2]) : 0;
  const meridiem = match[3].toLowerCase();

  if (meridiem === 'pm' && hour < 12) hour += 12;
  if (meridiem === 'am' && hour === 12) hour = 0;

  return `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}:00`;
}

function parseDuration(text) {
  const patterns = [
    { regex: /(\d+(?:\.\d+)?)\s*(?:hours?|hrs?)/i, multiplier: 60 },
    { regex: /(\d+(?:\.\d+)?)\s*(?:minutes?|mins?)/i, multiplier: 1 },
    { regex: /(\d{1,2}):(\d{2})/, handler: (match) => parseInt(match[1]) * 60 + parseInt(match[2]) }
  ];

  for (const pattern of patterns) {
    const match = text.match(pattern.regex);
    if (match) {
      if (pattern.handler) return pattern.handler(match);
      return Math.round(parseFloat(match[1]) * pattern.multiplier);
    }
  }
  return null;
}

function parseRelativeDate(text) {
  const now = new Date();
  const dayMap = {
    'today': 0, 'tomorrow': 1, 'yesterday': -1
  };

  // Simple day references
  for (const [key, offset] of Object.entries(dayMap)) {
    if (text.includes(key)) {
      now.setDate(now.getDate() + offset);
      return now.toISOString().split('T')[0];
    }
  }

  // "next X" patterns
  const nextMatch = text.match(/next\s+(\w+)/i);
  if (nextMatch) {
    const unit = nextMatch[1].toLowerCase();
    if (unit === 'week') now.setDate(now.getDate() + 7);
    else if (unit === 'month') now.setMonth(now.getMonth() + 1);
    else if (unit === 'year') now.setFullYear(now.getFullYear() + 1);
    return now.toISOString().split('T')[0];
  }

  // "in X days/weeks" patterns
  const inMatch = text.match(/in\s+(\d+)\s+(days?|weeks?|months?)/i);
  if (inMatch) {
    const num = parseInt(inMatch[1]);
    const unit = inMatch[2].toLowerCase();
    if (unit.startsWith('day')) now.setDate(now.getDate() + num);
    else if (unit.startsWith('week')) now.setDate(now.getDate() + (num * 7));
    else if (unit.startsWith('month')) now.setMonth(now.getMonth() + num);
    return now.toISOString().split('T')[0];
  }

  return null;
}

function extractEmotions(text) {
  const emotions = [];
  const emotionMap = {
    'happy': ['happy', 'excited', 'proud', 'great', 'amazing', 'fantastic'],
    'sad': ['sad', 'depressed', 'down', 'low', 'miserable'],
    'anxious': ['anxious', 'worried', 'nervous', 'scared', 'fear'],
    'frustrated': ['frustrated', 'annoyed', 'pissed', 'angry', 'mad'],
    'tired': ['tired', 'exhausted', 'drained', 'burnt out', 'fatigue'],
    'guilty': ['guilty', 'ashamed', 'bad about', 'regret'],
    'stressed': ['stressed', 'overwhelmed', 'pressure', 'tension'],
    'motivated': ['motivated', 'determined', 'focused', 'driven'],
    'accomplished': ['accomplished', 'achieved', 'succeeded', 'completed']
  };

  for (const [emotion, keywords] of Object.entries(emotionMap)) {
    if (keywords.some(keyword => text.includes(keyword))) {
      emotions.push(emotion);
    }
  }

  return [...new Set(emotions)]; // Remove duplicates
}

function detectTables(text) {
  const tables = new Set();
  const cleanedText = cleanTypos(text);

  // Primary detection
  for (const [table, keywords] of Object.entries(TRIGGERS)) {
    if (keywords.some(keyword => cleanedText.includes(keyword))) {
      tables.add(table);
    }
  }

  // Multi-table patterns
  if (cleanedText.includes('morning routine') && cleanedText.includes('missed')) {
    tables.add('routine');
    tables.add('common_problems');
  }

  if (cleanedText.match(/\$[\d,]+|earned|made.*\d+/) && !tables.has('business:sales')) {
    tables.add('business:sales');
  }

  if (cleanedText.includes('tired') && cleanedText.includes('work')) {
    tables.add('health');
    tables.add('performance');
  }

  if (cleanedText.includes('family') && cleanedText.includes('work')) {
    tables.add('family');
    tables.add('common_problems');
  }

  return Array.from(tables);
}

