  const channels = ['email', 'phone', 'linkedin', 'referral', 'website', 'cold call'];
  for (const channel of channels) {
    if (text.includes(channel)) {
      data.sales_channel = channel;
      break;
    }
  }

  // Time to close
  const timeMatch = text.match(/(\d+)\s*(?:days?|weeks?)\s*(?:to close|closing)/i);
  if (timeMatch) {
    data.time_to_close = parseInt(timeMatch[1]);
  }

  // SaaS metrics
  if (text.includes('mrr') || text.includes('monthly recurring')) {
    data.revenue_type = 'recurring';
  } else if (text.includes('one-time') || text.includes('one time')) {
    data.revenue_type = 'one-time';
  }

  // Churn/loss reasons
  if (text.includes('churn') || text.includes('cancelled')) {
    data.churn_reason = 'customer cancelled';
  } else if (text.includes('competitor')) {
    data.rejection_reason = 'chose competitor';
  }

  // Adjust confidence for emotional context
  if (emotions.includes('excited') && data.deal_stage === 'closed') {
    data.confidence_level = Math.min(data.confidence_level * 1.1, 1.0);
  } else if (emotions.includes('disappointed') && data.deal_stage === 'lost') {
    data.confidence_level = Math.min(data.confidence_level * 1.1, 1.0);
  } else if (hasConflictingEmotions(emotions)) {
    data.confidence_level *= 0.85;
  }

  return data;
}

