function extractHealth(text, emotions) {
  const data = {
    user_input: text,
    emotional_signals: emotions,
    confidence_level: 0.5
  };

  // Sleep hours
  const sleepPatterns = [
    /(\d+(?:\.\d+)?)\s*(?:hours?|hrs?)\s*(?:of\s*)?sleep/i,
    /slept\s*(?:for\s*)?(\d+(?:\.\d+)?)\s*(?:hours?|hrs?)/i,
    /got\s*(\d+(?:\.\d+)?)\s*(?:hours?|hrs?)/i
  ];

  for (const pattern of sleepPatterns) {
    const match = text.match(pattern);
    if (match) {
      data.sleep_hours = parseFloat(match[1]);
      data.confidence_level = calculateFieldConfidence(text, 'sleep_hours', match[1], 'explicit');
      break;
    }
  }

  // Stress level (1-10) with confidence based on intensity markers
  if (text.includes('overwhelmed') || text.includes('breaking point')) {
    data.stress_level = 9;
    data.confidence_level = calculateFieldConfidence(text, 'stress_level', 'high', 'explicit');
  } else if (text.includes('very stressed') || text.includes('extremely stressed')) {
    data.stress_level = 8;
    data.confidence_level = calculateFieldConfidence(text, 'stress_level', 'high', 'reported');
  } else if (text.includes('stressed') || text.includes('anxious')) {
    data.stress_level = 7;
    data.confidence_level = calculateFieldConfidence(text, 'stress_level', 'moderate', 'implied');
  } else if (text.includes('tense') || text.includes('pressure')) {
    data.stress_level = 6;
    data.confidence_level = calculateFieldConfidence(text, 'stress_level', 'moderate', 'inferred');
  } else if (text.includes('calm') || text.includes('relaxed')) {
    data.stress_level = 3;
    data.confidence_level = calculateFieldConfidence(text, 'stress_level', 'low', 'reported');
  }

  // Coffee intake
  const coffeeMatch = text.match(/(\d+)\s*(?:cups?\s*of\s*)?(?:coffee|espresso|latte|caffeine)/i);
  if (coffeeMatch) {
    data.caffeine_intake = parseInt(coffeeMatch[1]);
    // Higher confidence if "cups of coffee" vs just "3 coffee"
    const hasUnit = text.includes('cup') || text.includes('cups');
    data.confidence_level = calculateFieldConfidence(
      text, 
      'caffeine_intake', 
      coffeeMatch[1], 
      hasUnit ? 'explicit' : 'implied'
    );
  }

  // Water intake
  const waterMatch = text.match(/(\d+(?:\.\d+)?)\s*(?:liters?|litres?|l)\s*(?:of\s*)?water/i);
  if (waterMatch) {
    data.water_liters = parseFloat(waterMatch[1]);
    data.confidence_level = calculateFieldConfidence(text, 'water_liters', waterMatch[1], 'explicit');
  }

  // Meals
  const mealKeywords = ['breakfast', 'lunch', 'dinner', 'snack'];
  let mealCount = 0;
  let mealConfidence = 1.0;
  for (const meal of mealKeywords) {
    if (text.includes(meal)) {
      mealCount++;
      // Check for negation "skipped breakfast"
      const skippedPattern = new RegExp(`(skipped|missed|no)\\s+${meal}`, 'i');
      if (skippedPattern.test(text)) {
        mealCount--;
        mealConfidence *= 0.8;
      }
    }
  }
  if (mealCount > 0) {
    data.meals_count = mealCount;
    data.confidence_level = calculateFieldConfidence(text, 'meals_count', mealCount, 'reported') * mealConfidence;
  }

  // Energy crashes
  if (text.includes('crash') || text.includes('energy dip') || text.includes('afternoon slump')) {
    data.energy_crashes = 'afternoon';
    data.confidence_level = calculateFieldConfidence(text, 'energy_crashes', 'crash', 'explicit');
  }

  // Health issues with varying confidence
  if (text.includes('migraine')) {
    data.health_issue = 'headache';
    data.confidence_level = calculateFieldConfidence(text, 'health_issue', 'migraine', 'explicit');
  } else if (text.includes('headache')) {
    data.health_issue = 'headache';
    data.confidence_level = calculateFieldConfidence(text, 'health_issue', 'headache', 'reported');
  } else if (text.includes('head hurts')) {
    data.health_issue = 'headache';
    data.confidence_level = calculateFieldConfidence(text, 'health_issue', 'headache', 'implied');
  }

  // Ripple effects from emotions
  if (emotions.includes('exhausted')) {
    if (!data.sleep_hours) {
      data.sleep_quality = 'poor';
      // This is inferred from emotion, so lower confidence
      data.confidence_level = calculateRippleConfidence(0.7, 2);
    }
  }

  // Context boosting
  if (emotions.includes('tired') && data.sleep_hours && data.sleep_hours < 6) {
    // Emotion confirms the data
    data.confidence_level = Math.min(data.confidence_level * 1.15, 1.0);
  }

  return data;
}

function extractFitness(text, emotions) {
