  data.CommonProblems = text;

  return data;
}

function extractPerformance(text, emotions) {
  const data = {
    emotional_signals: emotions,
    confidence_level: 0.5
  };

  // Success patterns (including typos)
  const successPattern = /\b(sold|sld|made|maed|earned|earnd|achieved|achived|hit|reached|reched|completed|complted|finished|finishd|launched|lauched|shipped|shiped|closed|closd|won|gained|gaind)\b/i;

  // Failure patterns (including typos)
  const failurePattern = /\b(missed|misd|mised|failed|faild|lost|lst|couldn't|couldnt|didn't|didnt|unable|unabl|stuck|stuk|blocked|blockd|struggled|struggld)\b/i;

  // Check for money mentions with success words
  const hasMoneySuccess = (text.match(/\$[\d,]+/) || parseMoneyValue(text)) && successPattern.test(text);

  if (hasMoneySuccess || (successPattern.test(text) && !failurePattern.test(text))) {
    data.WhatWentWell = text;
    data.confidence_level = 0.9;
  } else if (failurePattern.test(text)) {
    data.WhatBlocked = text;
    data.confidence_level = 0.9;
  }

  // Tomorrow's hurdle
  if (text.includes('tomorrow') || text.includes('need to') || text.includes('have to')) {
    data.TomorrowHurdle = text;
  }

  // Energy level correlation
  if (text.includes('energized') || text.includes('pumped')) {
    data.energy_level = 'high';
  } else if (text.includes('tired') || text.includes('exhausted')) {
    data.energy_level = 'low';
  }

  return data;
}

function extractUserFeedback(text, emotions, sessionID, previousAIReply) {
  const data = {
    user_input: text,
    sessionID: sessionID,
    emotional_signals: emotions,
    confidence_level: 0.8
  };

  // AI suggestion responses
  if (previousAIReply) {
    data.ai_response = previousAIReply;

    // Acceptance patterns
    if (text.match(/\b(ok|okay|sure|will do|i'll try|good idea|makes sense|got it|thanks|helpful)\b/i)) {
      data.suggestion_accepted = true;
      data.feedback_sentiment = 'positive';
      data.confidence_level = 0.9;
    }

    // Rejection patterns
    else if (text.match(/\b(no|won't work|can't|already tried|doesn't help|bad idea|wrong)\b/i)) {
      data.suggestion_accepted = false;
      data.feedback_sentiment = 'negative';
      data.confidence_level = 0.9;
    }

    // Explanation patterns
    if (text.includes('because') || text.includes('but')) {
      data.reasoning_provided = text;
      data.has_context = true;
    }
  }

  // Meta feedback about Celeste7
  if (text.match(/\b(celeste|this ai|this app|this system|you)\b/i)) {
    data.category = 'system_feedback';

    if (text.match(/\b(love|great|amazing|helpful|life.?changing|game.?changer)\b/i)) {
      data.system_sentiment = 'very positive';
      data.nps_indicator = 9;
    } else if (text.match(/\b(like|good|useful|works)\b/i)) {
      data.system_sentiment = 'positive';
      data.nps_indicator = 7;
    } else if (text.match(/\b(hate|terrible|useless|waste|sucks|annoying)\b/i)) {
      data.system_sentiment = 'very negative';
      data.nps_indicator = 3;
    }
  }

  // Feature requests
  if (text.match(/\b(wish|want|need|should|could you|can you|would be nice)\b/i)) {
    data.category = 'feature_request';
    data.request_type = 'enhancement';
  }

  // Bug reports
  if (text.match(/\b(broken|bug|error|doesn't work|not working|crashed|glitch)\b/i)) {
    data.category = 'bug_report';
    data.request_type = 'fix';
  }

  // Success stories (LIQUID GOLD!)
  if (text.match(/\b(worked|helped|achieved|succeeded|finally|breakthrough|thanks to)\b/i)) {
    data.category = 'success_story';
    data.impact_reported = true;
  }

  // Transformation indicators (for brand pivot)
  if (text.match(/\b(transformed|changed my|inevitable|unstoppable|momentum|different person)\b/i)) {
    data.transformation_language = true;
    data.brand_alignment = 'new_positioning';
  } else if (text.match(/\b(harsh|brutal|truth|called out|exposed)\b/i)) {
    data.brand_alignment = 'old_positioning';
  }

  return data;
}

function extractBusinessModels(text, emotions) {
  const data = {
    user_input: text,
    emotional_signals: emotions,
    confidence_level: 0.5
  };

  // Business model type
  if (text.match(/\b(subscription|recurring|monthly|saas|sub)\b/i)) {
    data.business_type = 'subscription';
  } else if (text.match(/\b(marketplace|platform|commission|take rate)\b/i)) {
    data.business_type = 'marketplace';
  } else if (text.match(/\b(freemium|free tier|premium|upgrade)\b/i)) {
    data.business_type = 'freemium';
  } else if (text.match(/\b(licensing|license|royalty|franchise)\b/i)) {
    data.business_type = 'licensing';
  } else if (text.match(/\b(advertising|ads|ad revenue|sponsors)\b/i)) {
    data.business_type = 'advertising';
  } else if (text.match(/\b(one time|single purchase|product sales?)\b/i)) {
    data.business_type = 'one_time_purchase';
  }

  // Revenue model
  if (text.match(/\b(product sales?|selling products?)\b/i)) data.revenue_model = 'product_sales';
  else if (text.match(/\b(service|consulting|done for you)\b/i)) data.revenue_model = 'services';
  else if (text.match(/\b(subscription|recurring|monthly)\b/i)) data.revenue_model = 'subscriptions';
  else if (text.match(/\b(advertising|ads|sponsored)\b/i)) data.revenue_model = 'advertising';

  // Monthly revenue range
  const moneyValue = parseMoneyValue(text);
  if (moneyValue) {
    if (moneyValue >= 100000) data.monthly_revenue_range = '$100k+';
    else if (moneyValue >= 50000) data.monthly_revenue_range = '$50k-$100k';
    else if (moneyValue >= 25000) data.monthly_revenue_range = '$25k-$50k';
    else if (moneyValue >= 10000) data.monthly_revenue_range = '$10k-$25k';
    else if (moneyValue >= 5000) data.monthly_revenue_range = '$5k-$10k';
    else if (moneyValue >= 1000) data.monthly_revenue_range = '$1k-$5k';
    else data.monthly_revenue_range = '<$1k';
    data.confidence_level = 0.9;
  }

  // Business stage
  if (text.match(/\b(idea|concept|planning|just started)\b/i)) {
    data.business_stage = 'idea';
  } else if (text.match(/\b(launched|live|production|released)\b/i)) {
    data.business_stage = 'launched';
  } else if (text.match(/\b(scaling|growing|mature|established)\b/i)) {
    data.business_stage = 'scaling';
  }

  // Team size
  const teamMatch = text.match(/(?:team of|team size).*?(\d+)/i);
  if (teamMatch) {
    data.team_size = parseInt(teamMatch[1]);
  }

  return data;
}

function extractBusinessProduct(text, emotions) {
  const data = {
