  }

  // Development stage
  if (text.match(/\b(idea|concept|planning|just started)\b/i)) {
    data.development_stage = 'idea';
  } else if (text.match(/\b(prototype|prototyping|mockup|wireframe)\b/i)) {
    data.development_stage = 'prototype';
