    data.development_stage = 'beta';
  } else if (text.match(/\b(launched|live|production|released)\b/i)) {
    data.development_stage = 'launched';
  }

  // User feedback score
  const feedbackMatch = text.match(/([0-9.]+)(?:\/10|\/5|\s*stars?|\s*rating)/i);
  if (feedbackMatch) {
    data.user_feedback_score = parseFloat(feedbackMatch[1]);
    data.confidence_level = 0.9;
  }

  // Technical debt
  if (text.match(/\b(technical debt|legacy|messy|refactor|rewrite)\b/i)) {
