  // Content type
  if (text.match(/\b(video|youtube|yt|tiktok|reel|reels|shorts)\b/i)) {
    data.content_type = 'video';
  } else if (text.match(/\b(blog|article|post|writing|seo|content marketing)\b/i)) {
    data.content_type = 'blog';
  } else if (text.match(/\b(podcast|audio|spotify|apple podcasts)\b/i)) {
    data.content_type = 'podcast';
  } else if (text.match(/\b(newsletter|email|email marketing)\b/i)) {
    data.content_type = 'newsletter';
  }

  // Platform
  if (text.match(/\b(youtube|yt)\b/i)) data.platform = 'youtube';
  else if (text.match(/\b(instagram|ig|insta)\b/i)) data.platform = 'instagram';
  else if (text.match(/\b(tiktok|tt)\b/i)) data.platform = 'tiktok';
  else if (text.match(/\b(linkedin|li)\b/i)) data.platform = 'linkedin';
  else if (text.match(/\b(facebook|fb)\b/i)) data.platform = 'facebook';

  // Engagement rate
  const engagementMatch = text.match(/([0-9.]+)%?\s*(?:engagement|engage|engaging)/i);
  if (engagementMatch) {
    data.engagement_rate = parseFloat(engagementMatch[1]);
    data.confidence_level = 0.9;
  }

  // Follower count
  const followerMatch = text.match(/([0-9,]+k?)\s*(?:followers|subs|subscribers)/i);
  if (followerMatch) {
    let count = followerMatch[1].replace(/,/g, '');
    if (count.includes('k')) {
      count = parseFloat(count.replace('k', '')) * 1000;
    }
    data.follower_count = parseInt(count);
  }

  // Posting frequency
  if (text.match(/\b(daily|every day|each day)\b/i)) {
    data.posting_frequency = 'daily';
  } else if (text.match(/\b(weekly|once a week|per week)\b/i)) {
    data.posting_frequency = 'weekly';
  } else if (text.match(/\b(monthly|once a month|per month)\b/i)) {
    data.posting_frequency = 'monthly';
  }

  return data;
}

