function extractBusinessOperations(text, emotions) {
  const data = {
    user_input: text,
    emotional_signals: emotions,
    confidence_level: 0.5
  };

  // Automation level (1-10)
  if (text.match(/\b(fully automated|completely automated|100% automated)\b/i)) {
    data.automation_level = 9;
  } else if (text.match(/\b(mostly automated|highly automated)\b/i)) {
    data.automation_level = 7;
  } else if (text.match(/\b(some automation|partially automated)\b/i)) {
    data.automation_level = 5;
  } else if (text.match(/\b(manual|no automation|all manual)\b/i)) {
    data.automation_level = 1;
  }

  // System integrations
  const integrationsMatch = text.match(/(\d+)\s*(?:integrations|tools|systems|connected|apps)/i);
  if (integrationsMatch) {
    data.system_integrations = parseInt(integrationsMatch[1]);
  }

  // Error rate
  const errorMatch = text.match(/([0-9.]+)%?\s*(?:error|mistakes|wrong|failed)/i);
  if (errorMatch) {
    data.error_rate = parseFloat(errorMatch[1]);
  }

  // Response time
  const responseMatch = text.match(/([0-9.]+)\s*(?:hours?|hrs?|minutes?|mins?)\s*(?:response|reply)/i);
  if (responseMatch) {
    let time = parseFloat(responseMatch[1]);
    if (text.includes('minute') || text.includes('min')) {
      time = time / 60; // Convert to hours
    }
    data.customer_service_response_time_hours = time;
  }

  // Backup systems
  if (text.match(/\b(backup|backups|disaster recovery|redundancy)\b/i)) {
    data.backup_systems_in_place = !text.match(/\b(no backup|no backups|not backed up)\b/i);
  }

  return data;
}

function extractBusinessSaas(text, emotions) {
