    data.confidence_level = 0.9;
  }

  // ARR
  const arrMatch = text.match(/(?:arr|annual recurring).*?\$?([0-9,]+(?:\.[0-9]+)?k?)/i);
  if (arrMatch) {
    data.annual_recurring_revenue = parseMoneyValue(arrMatch[1]);
  }

  // Subscribers
  const subsMatch = text.match(/([0-9,]+k?)\s*(?:subscribers|users|customers|paying|active)/i);
  if (subsMatch) {
    let count = subsMatch[1].replace(/,/g, '');
    if (count.includes('k')) {
      count = parseFloat(count.replace('k', '')) * 1000;
    }
    data.active_subscribers = parseInt(count);
  }

  // Churn rate
  const churnMatch = text.match(/([0-9.]+)%?\s*(?:churn|cancel|churning|leaving)/i);
  if (churnMatch) {
