  // New signups
  const signupMatch = text.match(/(\d+)\s*(?:new|fresh)?\s*(?:signups|registrations|users)/i);
  if (signupMatch) {
    data.new_signups = parseInt(signupMatch[1]);
  }

  return data;
}

// ===== MAIN ROUTER FUNCTION =====
function routeMessage(input) {
  // Match the exact n8n merge structure
  const allInputs = $input.all();

  // Previous conversation from Postgres (position 0)
  const previousData = allInputs[0]?.json || {};
  const previousMessage = previousData.message || null;
  const previousAIReply = previousData.ai_reply || null;

  // Current message from webhook (position 1)
  const currentData = allInputs[1]?.json?.body || allInputs[1]?.json || {};
  const message = currentData.message;
  const userID = currentData.userID;
  const sessionID = currentData.sessionID;

  // Handle empty
  if (!message || message.trim() === '') {
    return [{
      table: "unknown_interactions",
      operation: "insert",
      data: {
        user_input: message || '',
        reason_unclassified: "empty message",
        confidence_level: 1.0,
        emotional_signals: []
      }
    }];
  }

  // Clean and analyze
  const cleanedMessage = cleanTypos(message.toLowerCase());
  const emotions = extractEmotions(cleanedMessage);
  const tables = detectTables(cleanedMessage);

  const results = [];

  // CRITICAL: Check if this is user feedback FIRST
  const isFeedback = previousAIReply && (
    message.match(/\b(yes|no|ok|sure|won't|can't|tried|but|because|works|doesn't work|thanks|helpful)\b/i) ||
    message.includes('celeste') ||
    message.length < 100 && tables.length === 0 // Short responses to AI are often feedback
  );

  if (isFeedback) {
    tables.push('user_feedback');
  }

  // Always add recent_interactions
  const primaryTable = tables[0] || 'general';
  results.push({
    table: "recent_interactions",
    operation: "insert",
    data: {
      user_id: userID,
      session_id: sessionID,
      interaction_type: "text-chat",
      content: message,
      category: primaryTable,
      metadata: {
        triggers: tables,
        ripples: tables.slice(1) || [],
        has_context: !!previousAIReply,
        is_feedback: isFeedback
      },
      emotional_signals: emotions,
      confidence_level: 1.0
    }
  });

  // Handle contextual responses
  if (previousAIReply) {
    // Pure number with context
    if (/^\d+$/.test(message.trim())) {
      if (previousAIReply.match(/how many|number of|count/i)) {
        if (previousAIReply.includes('deals') || previousAIReply.includes('sales')) {
          results.push({
            table: "business:sales",
            operation: "insert",
            data: {
              user_input: message,
              ai_context: previousAIReply,
              deals_count: parseInt(message),
              confidence_level: 0.95,
              emotional_signals: emotions
            }
          });
        } else if (previousAIReply.includes('coffee') || previousAIReply.includes('cups')) {
          results.push({
            table: "health",
            operation: "insert",
            data: {
              user_input: message,
              ai_context: previousAIReply,
              caffeine_intake: parseInt(message),
              confidence_level: 0.95,
              emotional_signals: emotions
            }
          });
        }
      }

      // Still route to user_feedback for tracking
      if (isFeedback) {
        results.push({
          table: "user_feedback",
          operation: "insert",
          data: extractUserFeedback(message, emotions, sessionID, previousAIReply)
        });
      }

      return results;
    }

    // Yes/No responses
    if (message.toLowerCase().match(/^(yes|no|yeah|nope|yep|nah|ok|okay|sure)$/)) {
      // Always capture as feedback
      results.push({
        table: "user_feedback",
        operation: "insert",
        data: extractUserFeedback(message, emotions, sessionID, previousAIReply)
      });

      // Also route to specific table if context is clear
      if (previousAIReply.includes('workout') || previousAIReply.includes('gym')) {
        results.push({
          table: "fitness",
          operation: "insert",
          data: {
            user_input: message,
            ai_context: previousAIReply,
            missed_planned_workout: message.match(/no|nope|nah/i) ? true : false,
            confidence_level: 0.9,
            emotional_signals: emotions
          }
        });
      }

      return results;
    }

    // "Because" responses - ALWAYS feedback gold
    if (message.toLowerCase().startsWith('because') || message.includes(' because ')) {
      results.push({
        table: "user_feedback",
        operation: "insert",
        data: {
          ...extractUserFeedback(message, emotions, sessionID, previousAIReply),
          category: 'reasoning',
          has_context: true,
          confidence_level: 1.0
        }
      });

      // Also extract to relevant domain tables
      const contextTables = detectTables(previousMessage + ' ' + message);
      for (const table of contextTables) {
        if (table !== 'user_feedback') {
          results.push({
            table: table,
            operation: "insert",
            data: {
              user_input: message,
              context_from_previous: previousMessage,
              reasoning_provided: message,
              emotional_signals: emotions,
              confidence_level: 0.7
            }
          });
        }
      }

      return results;
    }
  }

  // No triggers found
  if (tables.length === 0) {
    results.push({
      table: "unknown_interactions",
      operation: "insert",
      data: {
        user_input: message,
        reason_unclassified: "no triggers found",
        potential_categories: [],
        ai_context: previousAIReply,
        emotional_signals: emotions,
        confidence_level: 0.3
      }
    });
    return results;
  }

  // Route to all detected tables
  for (const table of tables) {
    let data = {};

    switch(table) {
      case 'business:sales':
        data = extractBusinessSales(message, emotions);
        break;
      case 'business:networking':
        data = extractBusinessNetworking(message, emotions);
        break;
      case 'business:advertisments':
        data = extractBusinessAdvertisements(message, emotions);
        break;
      case 'business:models':
        data = extractBusinessModels(message, emotions);
        break;
      case 'business:product':
        data = extractBusinessProduct(message, emotions);
        break;
      case 'business:agency':
        data = extractBusinessAgency(message, emotions);
        break;
      case 'business:content':
        data = extractBusinessContent(message, emotions);
        break;
      case 'business:ecommerce':
        data = extractBusinessEcommerce(message, emotions);
        break;
      case 'business:operations':
        data = extractBusinessOperations(message, emotions);
        break;
      case 'business:saas':
        data = extractBusinessSaas(message, emotions);
        break;
      case 'health':
        data = extractHealth(message, emotions);
        break;
      case 'fitness':
        data = extractFitness(message, emotions);
        break;
      case 'routine':
        data = extractRoutine(message, emotions);
        break;
      case 'goals':
        data = extractGoals(message, emotions);
        break;
      case 'emotions':
        data = extractEmotionsTable(message, emotions);
        break;
      case 'family':
        data = extractFamily(message, emotions);
        break;
      case 'leverage':
        data = extractLeverage(message, emotions);
        break;
      case 'common_problems':
        data = extractCommonProblems(message, emotions);
        break;
      case 'performance':
        data = extractPerformance(message, emotions);
        break;
      case 'user_feedback':
        data = extractUserFeedback(message, emotions, sessionID, previousAIReply);
        break;
      default:
        data = {
          user_input: message,
          emotional_signals: emotions,
          confidence_level: 0.5
        };
    }

    // Add context if available
    if (previousAIReply && !data.ai_context) {
      data.ai_context = previousAIReply;
    }

    results.push({
      table: table,
      operation: "insert",
      data: data
    });
  }

  return results;
}

// ===== EXPORT FOR N8N =====
// No need for complex handling - routeMessage now expects the merged structure
return routeMessage($input);
