// Simple rate limiter with user isolation
const userId = $json.body.userId;
const now = Date.now();
const WINDOW = 60000; // 1 minute
const MAX_REQUESTS = 20;

// Initialize if needed
if (!global.rateLimits) {
  global.rateLimits = new Map();
}

// Clean old entries every 5 minutes
if (!global.lastCleanup || now - global.lastCleanup > 300000) {
  for (const [key, data] of global.rateLimits.entries()) {
    if (now - data.windowStart > WINDOW * 2) {
      global.rateLimits.delete(key);
    }
  }
  global.lastCleanup = now;
}

// Check user's rate
let userLimit = global.rateLimits.get(userId);
if (!userLimit || now - userLimit.windowStart > WINDOW) {
  userLimit = { windowStart: now, count: 1 };
  global.rateLimits.set(userId, userLimit);
} else {
  userLimit.count++;
}

if (userLimit.count > MAX_REQUESTS) {
  throw new Error('Rate limit exceeded. Please wait a moment.');
}

return {
  json: {
    ...$json,
    timestamp: now
  }
};

// Fallback context for new users or DB errors
return {
  json: {
    user_id: $json.body.userId,
    user_email: $json.body.user.email,
    user_name: $json.body.user.displayName,
    current_message: $json.body.message,
