// CELESTE7 BEAST MODE ORACLE - PRODUCTION VERSION
// Calls your live Vercel API instead of doing ML in n8n

async function beastModeOracle(context, aiResponse) {
  const ORACLE_API_URL = 'https://code-gules-tau.vercel.app';
  const userId = context.user_id;
  const message = context.current_message;

  try {
    // 1. ANALYZE THE MESSAGE WITH YOUR ORACLE API
    console.log('Calling Oracle API for pattern analysis...');

    const analyzeResponse = await fetch(`${ORACLE_API_URL}/api/analyze`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        userId: userId,
        message: message,
        context: {
          businessType: context.business_model_type || 'unknown',
          energyLevel: context.current_energy || 'medium',
          trustLevel: context.trust_level || 5,
          recentPatterns: context.recent_patterns || [],
          messagesLastHour: context.messages_last_hour || 0
        }
      })
    });

    if (!analyzeResponse.ok) {
      throw new Error(`Oracle API error: ${analyzeResponse.status}`);
    }

    const analysis = await analyzeResponse.json();

    // 2. CHECK IF WE SHOULD ENHANCE
    if (!analysis.success || !analysis.data?.patterns?.length) {
      console.log('No patterns detected, returning original response');
      return {
        enhanced_response: aiResponse,
        original_response: aiResponse,
        analysis: { patterns: [], intervened: false }
      };
    }

    const primaryPattern = analysis.data.patterns[0];
    console.log(`Pattern detected: ${primaryPattern.name} (${primaryPattern.confidence})`);

    // 3. ENHANCE THE RESPONSE IF HIGH CONFIDENCE
    if (primaryPattern.confidence < 0.7) {
      console.log('Confidence too low, skipping enhancement');
      return {
        enhanced_response: aiResponse,
        original_response: aiResponse,
        analysis: analysis.data,
        pattern_detected: primaryPattern.name,
        confidence: primaryPattern.confidence,
        intervened: false
      };
    }

    // 4. GET ENHANCED RESPONSE FROM ORACLE API
    console.log('Requesting response enhancement...');

    const enhanceResponse = await fetch(`${ORACLE_API_URL}/api/enhance`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        userId: userId,
        message: message,
        aiResponse: aiResponse,
        context: {
          businessType: context.business_model_type || 'unknown',
          energyLevel: context.current_energy || 'medium',
          trustLevel: context.trust_level || 5,
          pattern: primaryPattern
        }
      })
    });

    if (!enhanceResponse.ok) {
      console.error('Enhancement failed, using fallback');
      // Fallback to simple enhancement
      const fallbackEnhancement = getFallbackIntervention(primaryPattern.name, context);
      return {
        enhanced_response: aiResponse + "\n\n" + fallbackEnhancement,
        original_response: aiResponse,
        analysis: analysis.data,
        pattern_detected: primaryPattern.name,
        confidence: primaryPattern.confidence,
        intervened: true,
        fallback: true
      };
    }

    const enhancement = await enhanceResponse.json();

    // 5. RETURN ENHANCED RESPONSE
    return {
      enhanced_response: enhancement.data?.response || aiResponse,
      original_response: aiResponse,
      analysis: analysis.data,
      pattern_detected: primaryPattern.name,
      confidence: primaryPattern.confidence,
      intervened: enhancement.data?.enhanced || false,
      enhancement_id: enhancement.data?.id
    };

  } catch (error) {
    console.error('Oracle error:', error);

    // GRACEFUL FALLBACK - Don't break the conversation
    return {
      enhanced_response: aiResponse,
      original_response: aiResponse,
      error: error.message,
      intervened: false,
      fallback: true
    };
  }
}

